import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TodoItem from './TodoItem';
import AddTodo from './AddTodo';

const TodoList = () => {
    const [todos, setTodos] = useState([]);

    useEffect(() => {
        const fetchTodos = async () => {
            const res = await axios.get('http://localhost:5000/api/todos');
            setTodos(res.data);
        };
        fetchTodos();
    }, []);

    const addTodo = async (title) => {
        const res = await axios.post('http://localhost:5000/api/todos', { title });
        setTodos([...todos, res.data]);
    };

    const toggleComplete = async (id) => {
        const todo = todos.find(todo => todo._id === id);
        const res = await axios.put(`http://localhost:5000/api/todos/${id}`, { title: todo.title, completed: !todo.completed });
        setTodos(todos.map(todo => todo._id === id ? res.data : todo));
    };

    const deleteTodo = async (id) => {
        await axios.delete(`http://localhost:5000/api/todos/${id}`);
        setTodos(todos.filter(todo => todo._id !== id));
    };

    return (
        <div>
            <AddTodo addTodo={addTodo} />
            {todos.map(todo => (
                <TodoItem key={todo._id} todo={todo} onToggleComplete={toggleComplete} onDelete={deleteTodo} />
            ))}
        </div>
    );
};

export default TodoList;
